css:
.print-format {
    font-family: Inter, "Helvetica Neue", Helvetica, Arial, "Open Sans", sans-serif;
    font-size: 14.5px; 
    color: #000;
    line-height: 1.4;
    padding: 30px;
}

h2 {
    font-weight: bold;
    font-size: 20px;
    margin: 0 0 5px 0;
}

.doc-name {
    font-size: 14px;
    margin-bottom: 25px;
}


.row-grid {
    display: flex;
    flex-wrap: wrap;
    margin-bottom: 20px;
}

.col-1 { width: 50%; }
.col-2 { width: 33.33%; }


.data-inline {
    margin-bottom: 8px;
    display: flex;
}
.data-inline .label {
    width: 140px; 
    color: #444;
}
.data-inline .value {
    font-weight: 500;
}

.data-stacked {
    margin-bottom: 15px;
}
.data-stacked .label {
    color: #555;
    margin-bottom: 2px;
}
.data-stacked .value {
    font-weight: 500;
    font-size: 12px;
}

.totals-section {
    display: flex;
    justify-content: space-between;
    margin-top: 30px;
}

.totals-table {
    width: 45%;
}

.total-row {
    display: flex;
    justify-content: space-between;
    padding: 4px 0;
}


.footer-sig {
    margin-top: 80px;
    display: flex;
    justify-content: space-between;
}
.sig-line {
    width: 35%;
    border-top: 1px solid #999;
}

@media print {
    .print-format { padding: 0; }
}
