css:
table,
tr,
td,
th {
  border: none !important;
  border-collapse: collapse !important;
}

.print-format {
  font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
  color: #192734;
  line-height: 1.5;
  padding: 4%;
  margin: 0;
  box-sizing: border-box;
  font-size: 13px;
}
h1 {
  font-size: 24px;
  font-weight: bold;
}
.doc-name {
  font-size: 13px;
  color: #4c5a67;
  margin-bottom: 15px;
}
.layout-table {
  width: 100%;
  border-collapse: collapse;
  table-layout: auto;
  margin-bottom: 8px;
}
.layout-table td {
  vertical-align: top;
}
.item-inline {
  margin-bottom: 8px;
}
.item-inline .label {
  font-size: 13px;
  color: #4c5a67;
  font-weight: normal;
  margin-bottom: 8px;
}

.item-inline .value {
  color: #192734;
  font-size: 13px;
  font-weight: normal;
  line-height: 1.8;
  margin-bottom: 8px;
}
.item-stacked {
  margin-left: 0;
  padding-left: 0;
  margin-bottom: 8px;
}
.item-stacked .label {
  font-weight: normal;
  color: #4c5a67;
  margin-bottom: 3px;
  font-size: 13px;
  display: block;
}
.item-stacked .value {
  font-weight: normal;
  color: #192734;
  font-size: 13px;
  margin-left: 10px;
  display: block;
  line-height: 1.8;
}
.item-inline-conditional .label {
  font-size: 13px;
  color: #4c5a67;
  font-weight: normal;
  margin-bottom: 8px;
  display: inline-block;
}
.item-inline-conditionaprint-format,
.print-format * {
  border: none !important;
  box-shadow: none !important;
  outline: none !important;
}
l .value {
  color: #192734;
  font-size: 13px;
  font-weight: normal;
  line-height: 1.8;
  margin-bottom: 8px;
  display: inline-block;
}

.layout-table .label {
  text-align: left;
}
.totals-table td {
  font-size: 13px;
  padding: 888 3px 0;
}

.schedule-part {
  margin-top: 30px;
  margin-bottom: 15px;
}
.schedule-part .label {
  font-size: 13px;
  color: #4c5a67;
  font-weight: normal;
  margin-bottom: 8px;
  display: inline-block;
  width: 250px;
}
.footer-sig {
  margin-top: 100px;
  display: inline-block;
  justify-content: center;
  width: 30%;
  text-align: center;
}
.signature-container {
  display: flex;
  justify-content: space-between;
  margin-top: 80px; /* Space above the signature section */
  padding: 0 40px; /* Padding from page edges */
}

.signature-box {
  width: 30%; /* Length of the line */
  text-align: center;
}

.signature-spacer {
  width: 30%; /* Gap between the two lines */
}

.signature-line {
  /* This creates the horizontal line */
  border-top: 1px solid #999 !important;
  height: 1px;
  margin-bottom: 5px;
}

.signature-label {
  font-size: 11px;
  color: #666;
  text-transform: uppercase;
  margin-top: 8px;
}

/* Ensure lines show up in PDF generation */
@media print {
  .signature-line {
    border-top: 1px solid #000 !important;
    -webkit-print-color-adjust: exact;
  }
}
